mapper.select:
select *
from photos a
where photo_id = #{photo_id}

SELECT a.item_id, a.owner_id, (SELECT owner_name FROM bas_owner WHERE owner_id = a.owner_id) as owner_name, a.item_code, a.item_name, a.logogram, a.item_desc, a.manufacturer, a.origin, a.stock_env, a.item_classify, a.item_category, a.item_abc, a.item_tips, a.approval_no, a.remark, a.is_enable, a.create_at, a.update_at, bpi.barcode, nvl((select su.user_name from sys_login_agent sla inner join sys_user su on su.user_id = sla.user_id where to_char(sla.login_id) = a.create_by), a.create_by) create_by, nvl((select su.user_name from sys_login_agent sla inner join sys_user su on su.user_id = sla.user_id where to_char(sla.login_id) = a.update_by), a.update_by) update_by FROM bas_item a LEFT JOIN bas_package_items bpi ON bpi.item_id = a.item_id AND bpi.package_level = 1 where a.is_enable = 1 --@ and a.owner_id = #{owner_id} --@ and a.item_classify = #{item_classify} --% and (a.item_code like #{item_code} or a.approval_no like #{item_code} or bpi.barcode like #{item_code}) --% and a.item_name like #{item_name}
and a.owner_id in
#{item}
--@ AND a.item_id = #{item_id}

mapper.update:
update RULE_APPLY a set a.match_type = #{match_type},a.rule_type = #{rule_type},
a.rule_id = #{rule_id},a.is_enable = #{is_enable}
where 1 = 1
--@ AND a.MATCH_ID = #{item_id}
