mapper.select:
select t.order_out_code, t.order_out_id, t.barcode, t.owner_code, t.wh_code, t.wh_name, t.customer_code, t.customer_name, t.address, t.review_confirm_time, t.has_upload from ( select distinct co.review_confirm_time, co.BARCODE, oo.ORDER_OUT_CODE, oo.ORDER_OUT_ID, bo.owner_code, bw.wh_code, bw.wh_name, bc.customer_code, bc.customer_name, bc.address, decode(dd.order_out_id, NULL, 0, 1) has_upload from CMD_OUT co inner join BAS_ITEM bi on co.ITEM_ID = bi.ITEM_ID inner join ORDER_OUT oo on co.ORDER_ID = oo.ORDER_OUT_ID inner join bas_owner bo on bo.owner_id = co.owner_id inner join bas_wh bw on bw.wh_id = co.wh_id left join bas_customer bc on bc.customer_id = oo.customer_id left join order_load_upload_records dd on dd.order_out_id = co.ORDER_ID and dd.barcode = co.barcode where STOCK_ENV = 4 and co.BARCODE != 'N/A' and co.CMD_STATE >= 90 and not exists (select 1 from CMD_OUT rt where co.ORDER_ID = rt.ORDER_ID and rt.CMD_STATE < 90)
union
select distinct co.review_confirm_time, co.BARCODE, oo.ORDER_OUT_CODE, oo.ORDER_OUT_ID, bo.owner_code, bw.wh_code, bw.wh_name, bc.customer_code, bc.customer_name, bc.address, decode(dd.order_out_id, NULL, 0, 1) has_upload from WMS8_HIS.CMD_OUT co inner join BAS_ITEM bi on co.ITEM_ID = bi.ITEM_ID inner join WMS8_HIS.ORDER_OUT oo on co.ORDER_ID = oo.ORDER_OUT_ID inner join bas_owner bo on bo.owner_id = co.owner_id inner join bas_wh bw on bw.wh_id = co.wh_id left join bas_customer bc on bc.customer_id = oo.customer_id left join order_load_upload_records dd on dd.order_out_id = co.ORDER_ID and dd.barcode = co.barcode where STOCK_ENV = 4 and co.BARCODE != 'N/A' and co.CMD_STATE >= 90) t where 1 = 1
AND to_char(t.review_confirm_time,'yyyy-mm-dd hh:mm:ss') >= #{start_time}
AND to_char(t.review_confirm_time,'yyyy-mm-dd hh:mm:ss') < #{end_time}
AND t.order_out_code = #{order_out_code}
AND (t.customer_code like #{customer_code} or t.customer_name like #{customer_code})
AND t.has_upload = #{has_upload}

select * from HXOA_QAS.dbo.material_delivery where 1=1
AND dn_number like '%' + #{dn_number} + '%'
AND box_number like '%' + #{box_number} + '%'
AND deliver_time >= #{start_time}
AND deliver_time < #{end_time}
AND company_code in (#{owner_id_list})

select * from HXOA_QAS.dbo.material_receive
where 1=1

    AND dn_number like '%' + #{dn_number} + '%'


    AND box_number like '%' + #{box_number} + '%'


    AND receive_time >= #{start_time}


    AND receive_time  <  #{end_time}

select t1.*, t2.customer_name from wms_material_delivery t1 left join bas_customer t2 on t1.receiver = t2.customer_code and t2.custom_type = 2
where 1=1

    AND t1.dn_number like '%' + #{dn_number} + '%'


    AND t1.box_number like '%' + #{box_number} + '%'


    AND t1.deliver_time >= #{start_time}


    AND t1.deliver_time  <  #{end_time}


    AND t1.id = #{id}

select t1.*, t2.customer_name from wms_material_receive t1 left join bas_customer t2 on t1.receiver = t2.customer_code and t2.custom_type = 1
where 1=1

    AND t1.dn_number like '%' + #{dn_number} + '%'


    AND t1.box_number like '%' + #{box_number} + '%'


    AND t1.receive_time >= #{start_time}


    AND t1.receive_time  <  #{end_time}


    AND t1.id = #{id}
