mapper.select:
select distinct oi.order_in_id, oi.order_in_code, bi.item_name, bi.item_code, bi.origin, CASE bi.item_specification WHEN 'N/A' then '*' ELSE bi.item_specification END as item_specification, ci.lot_id, (select lot01 from bas_lot_att bla where bla.lot_id = ci.lot_id) lot01, p.photo_type, p.path, bc.customer_code, bc.customer_name from (select * from order_in union all select * from wms8_his.order_in) oi inner join (select * from cmd_in union all select * from wms8_his.cmd_in ) ci on ci.order_id = oi.order_in_id inner join bas_item bi on bi.item_id = ci.item_id left join photos p on p.order_id = oi.order_in_id left join bas_customer bc on bc.customer_id = p.supplier_id where p.photo_type = 1
AND (bi.item_name LIKE '%'||#{itemInfo}||'%' OR bi.item_code LIKE '%'||#{itemInfo}||'%' OR UPPER(bi.logogram) LIKE '%'||#{itemInfo}||'%')
--@ AND oi.order_in_code = #{order_in_code} --@ AND p.photo_type = #{photo_type}

select distinct bi.item_name, p.photo_type, p.path, bla.lot_id, bla.lot01, bi.item_code, bi.origin, bc.customer_code, bc.customer_name, CASE bi.item_specification WHEN 'N/A' then '*' ELSE bi.item_specification END as item_specification from bas_item bi inner join bas_lot_att bla on bi.item_id = bla.item_id left join photos p on p.item_id = bi.item_id and p.lot_id = bla.lot_id left join bas_customer bc on bc.customer_id = p.supplier_id where p.photo_type = 1
AND (bi.item_name LIKE '%'||#{itemInfo}||'%' OR bi.item_code LIKE '%'||#{itemInfo}||'%' OR UPPER(bi.logogram) LIKE '%'||#{itemInfo}||'%')
--@ AND bla.lot01 = #{lot01} --@ AND p.photo_type = #{photo_type}
