mapper.select:
select a.*,
       b.customer_code,
       b.customer_name,
       c.owner_code,
       c.owner_name
from order_out a
     inner join bas_customer b on a.customer_id = b.customer_id
     inner join bas_owner c on c.owner_id = a.owner_id
where a.wh_id = #{wh_id}
  and a.status = #{status}
  and a.order_biz_type not in (24)
  --@ and a.dates >= #{start_date}
  --@ and a.dates <= #{end_date}
  --@ and a.owner_id = #{owner_id}
  --@ and a.order_out_code = #{order_out_code}
  --@ and a.order_type = #{order_type}
  --@ and a.order_biz_type = #{order_biz_type}
  --@ and a.remark = #{remark}
order by a.order_out_code

select wh_name
from bas_wh
where wh_id = #{wh_id}

select a.*
from order_wave a
where a.wh_id = #{wh_id}
      --@ and a.dates >= #{start_date}
      --@ and a.dates <= #{end_date}
      --@ and a.wave_no = #{wave_no}
order by a.wave_no

select a.wave_id from order_wave a inner join order_wave_details b on a.wave_id = b.wave_id
a.status = #{status} and a.wave_id in
#{item}

select a.order_out_id
from order_out a
where a.wave_id = #{wave_id}
